import java.util.*;

public class WordClassifier {
    private static List<String> knownWords = Arrays.asList("le", "chat", "mange", "un", "livre", "beau");

    private static boolean isWordKnown(String word) {
        return knownWords.contains(word);
    }

    private static Map<String, Boolean> classifySentence(String sentence) {
        String[] words = sentence.split(" ");
        Map<String, Boolean> classifiedWords = new HashMap<>();
        for (String word : words) {
            classifiedWords.put(word, isWordKnown(word));
        }
        return classifiedWords;
    }

    public static void main(String[] args) {
        if (args.length == 0) {
            System.out.println("Veuillez entrer une phrase comme argument de la ligne de commande.");
            return;
        }

        Map<String, Boolean> classifiedWords = classifySentence(args[0]);
        for (Map.Entry<String, Boolean> entry : classifiedWords.entrySet()) {
            System.out.println("Mot: " + entry.getKey() + ", Connue: " + entry.getValue());
        }
    }
}
