use std::collections::HashMap;
use std::env;

fn is_word_known(word: &str, known_words: &[&str]) -> bool {
    known_words.contains(&word)
}

fn classify_sentence(sentence: &str, known_words: &[&str]) -> HashMap<&str, bool> {
    let words: Vec<&str> = sentence.split_whitespace().collect();
    let mut classified_words: HashMap<&str, bool> = HashMap::new();
    for word in words {
        classified_words.insert(word, is_word_known(word, known_words));
    }
    classified_words
}

fn main() {
    let args: Vec<String> = env::args().collect();
    if args.len() <= 1 {
        println!("Veuillez entrer une phrase comme argument de la ligne de commande.");
        return;
    }

    let known_words: [&str; 6] = ["le", "chat", "mange", "un", "livre", "beau"];
    let classified_words: HashMap<&str, bool> = classify_sentence(&args[1], &known_words);
    for (key, value) in &classified_words {
        println!("Mot: {}, Connue: {}", key, value);
    }
}
