```python
import sys

known_words = ["le", "chat", "mange", "un", "livre", "beau"]

def is_word_known(word):
    return word in known_words

def classify_sentence(sentence):
    words = sentence.split()
    return {word: is_word_known(word) for word in words}

if __name__ == "__main__":
    if len(sys.argv) != 2:
        print("Veuillez entrer une phrase comme argument de la ligne de commande.")
    else:
        sentence = sys.argv[1]
        classified_words = classify_sentence(sentence)
        for word, is_known in classified_words.items():
            print(f"Mot: {word}, Connue: {is_known}")

