#include <iostream>
#include <string>
#include <sstream>
#include <unordered_map>
#include <algorithm>
#include <vector>

std::vector<std::string> known_words = {"le", "chat", "mange", "un", "livre", "beau"};

bool is_word_known(std::string word) {
    return std::find(known_words.begin(), known_words.end(), word) != known_words.end();
}

std::unordered_map<std::string, bool> classify_sentence(std::string sentence) {
    std::unordered_map<std::string, bool> classified_words;
    std::istringstream iss(sentence);
    std::string word;
    while (iss >> word) {
        classified_words[word] = is_word_known(word);
    }
    return classified_words;
}

int main(int argc, char** argv) {
    if (argc != 2) {
        std::cout << "Veuillez entrer une phrase comme argument de la ligne de commande.\n";
        return 1;
    }
    std::string sentence = argv[1];
    std::unordered_map<std::string, bool> classified_words = classify_sentence(sentence);
    for (auto const& pair: classified_words) {
        std::cout << "Mot: " << pair.first << ", Connue: " << (pair.second ? "true" : "false") << '\n';
    }
    return 0;
}

