import javax.jms.*;
import javax.naming.InitialContext;

public class JMSProducer {
    public static void main(String[] args) {
        Connection connection = null;
        try {
            InitialContext ctx = new InitialContext();

            ConnectionFactory factory = (ConnectionFactory) ctx.lookup("jms/ConnectionFactory");
            Destination destination = (Destination) ctx.lookup("jms/Queue");
// Topic topic = (Topic) ctx.lookup("jms/Topic");

            connection = factory.createConnection();

            Session session = connection.createSession(false, Session.AUTO_ACKNOWLEDGE);

            MessageProducer producer = session.createProducer(destination);

            TextMessage message = session.createTextMessage("Hello JMS!");
            producer.send(message);

            System.out.println("Message envoy√© : " + message.getText());

            producer.close();
            session.close();
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            if (connection != null) {
                try {
                    connection.close();
                } catch (JMSException e) {
                    e.printStackTrace();
                }
            }
        }
    }
}
