PipedOutputStream output = new PipedOutputStream();
PipedInputStream input = new PipedInputStream(output);

new Thread(() -> {
    try {
        output.write("Message 1".getBytes());
        output.close();
    } catch (IOException e) {
        e.printStackTrace();
    }
}).start();

new Thread(() -> {
    try {
        int data;
        while ((data = input.read()) != -1) {
            System.out.print((char) data);
        }
        input.close();
    } catch (IOException e) {
        e.printStackTrace();
    }
}).start();
