final UnitExerciseType exerciseType = new UnitExerciseType();
final Instant now = Instant.parse("2025-01-01T00:00:00Z");

@Test
void test() {
    try (MockedStatic<Instant> instantMockedStatic = Mockito.mockStatic(Instant.class)) {
        instantMockedStatic.when(Instant::now).thenReturn(now);
        AttemptLog attemptLog = new AttemptLog(0, new ArrayList<>());
        assertEquals(now, attemptLog.earliestNextAttempt(exerciseType.getRefractoryMinutes()));
    }
}
